mapper.select:
SELECT ROW_NUMBER() OVER(ORDER BY REG_DATE DESC) rnum ,R1.* FROM( SELECT ner.ERASURE_REQUEST_IDX as REQUEST_IDX ,ORG_NM ,ner.POSSESSION_NM ,ner.ITEM_NO ,ner.ITEM_DETAIL_NO ,ner.ITEM_NM ,ner.REG_USER ,ner.REG_DATE ,'erasure' ,ner.APPROVAL_STATE ,ner.REFUSAL_REASON FROM nam_erasure_request ner WHERE 1=1 ORDER by REG_DATE DESC ) R1 WHERE 1=1 AND org_nm = #{org_nm}
AND possession_nm = #{possession_nm}
AND (item_no LIKE CONCAT('%', #{item_no}, '%'))
AND (item_detail_no LIKE CONCAT('%', #{item_detail_no}, '%'))
AND approval_state = #{approval_state}
AND approval_state = #{approval_state}
AND approval_state = #{approval_state}
AND (REG_DATE BETWEEN #{start_date} and #{end_date})
AND (item_no BETWEEN CONVERT(#{start_item_no}, SIGNED) and CONVERT(#{end_item_no}, SIGNED))
LIMIT #{perPageNum} OFFSET #{pageStart}

SELECT ROW_NUMBER() OVER(ORDER BY REG_DATE DESC) rnum ,R1.* FROM( SELECT nkr.KEYWORD_REQUEST_IDX as REQUEST_IDX ,(SELECT noc.ORG_NM from nam_org_code noc WHERE noc.ORG_CODE_IDX = nib.ORG_CODE_IDX) as ORG_NM ,(SELECT POSSESSION_NM from nam_possession_code npc where POSSESSION_CODE_IDX = nib.POSSESSION_CODE_IDX ) as POSSESSION_NM ,nib.ITEM_NO ,nib.ITEM_DETAIL_NO ,nib.ITEM_NM ,nkr.REG_USER ,nkr.REG_DATE ,'keyword' ,nkr.APPROVAL_STATE ,nkr.REFUSAL_REASON FROM nam_keyword_request nkr join nam_item_base nib on nkr.ITEM_IDX = nib.ITEM_IDX WHERE 1=1 ORDER by REG_DATE DESC ) R1 WHERE 1=1 AND org_nm = #{org_nm}
AND possession_nm = #{possession_nm}
AND (item_no LIKE CONCAT('%', #{item_no}, '%'))
AND (item_detail_no LIKE CONCAT('%', #{item_detail_no}, '%'))
AND approval_state = #{approval_state}
AND approval_state = #{approval_state}
AND approval_state = #{approval_state}
AND (REG_DATE BETWEEN #{start_date} and #{end_date})
AND (item_no BETWEEN CONVERT(#{start_item_no}, SIGNED) and CONVERT(#{end_item_no}, SIGNED))
LIMIT #{perPageNum} OFFSET #{pageStart}
