mapper.select:
SELECT R1.* FROM( SELECT row_number() over(order by error_fix_idx desc) as rnum ,nef.error_fix_idx ,nef.error_fix_title ,nef.error_fix_content ,nef.error_fix_org_nm ,nef.board_type ,nef.count ,nef.reg_user ,DATE_FORMAT(nef.reg_date, '%Y-%m-%d %H:%i') AS reg_date ,nef.mod_user ,DATE_FORMAT(nef.mod_date, '%Y-%m-%d %H:%i') AS mod_date FROM nam_error_fix nef WHERE 1=1 AND ERROR_FIX_IDX = #{error_fix_idx}
AND (nef.error_fix_org_nm LIKE CONCAT('%', #{search_word}, '%'))
AND (nef.board_type = CASE WHEN '오류신고' LIKE CONCAT('%', #{search_word}, '%') THEN 'E' WHEN '개선사항' LIKE CONCAT('%', #{search_word}, '%') THEN 'F' END )
AND (nef.reg_user LIKE CONCAT('%', #{search_word}, '%'))
AND (nef.error_fix_org_nm LIKE CONCAT('%', #{search_word}, '%') OR nef.board_type = CASE WHEN '오류신고' LIKE CONCAT('%', #{search_word}, '%') THEN 'E' WHEN '개선사항' LIKE CONCAT('%', #{search_word}, '%') THEN 'F' END OR nef.reg_user LIKE CONCAT('%', #{search_word}, '%'))
) R1 LIMIT #{perPageNum} OFFSET #{pageStart}

mapper.insert:

 INSERT INTO nam_error_fix 
       ( ERROR_FIX_TITLE
       , ERROR_FIX_CONTENT
       , ERROR_FIX_ORG_NM
       , BOARD_TYPE
       , COUNT
       , REG_USER
       , REG_DATE)
VALUES ( #{error_fix_title}
       , #{error_fix_content}
       , #{error_fix_org_nm}
       , #{board_type}
       , 0
       , #{reg_user}
       , now())

mapper.update:

UPDATE nam_error_fix 
   SET ERROR_FIX_TITLE=#{error_fix_title}
     , ERROR_FIX_CONTENT=#{error_fix_content}
     , ERROR_FIX_ORG_NM=#{error_fix_org_nm}
     , BOARD_TYPE=#{board_type}
     , MOD_USER=#{mod_user}
 WHERE ERROR_FIX_IDX=#{error_fix_idx}
