mapper.select:
select * from nam_image where image_idx in (

		#{item}

	)

select row_number() over(order by convert(item_no, signed) desc) as rownum, nib.item_idx as item_idx, npc.POSSESSION_NM, noc.ORG_NM, nib.ITEM_NO as item_no, nib.ITEM_DETAIL_NO as ITEM_DETAIL_NO, ni.IMAGE_NM, ni.IMAGE_PATH, ni.PUBLIC_SERVICE , ni.image_idx from nam_item_base nib, nam_image ni, nam_org_code noc, nam_possession_code npc where nib.REG_STATE = #{reg_state} and nib.org_code_idx = #{org_code_idx}
and nib.possession_code_idx = #{possession_code_idx}
and nib.ITEM_NO >= convert(#{item_no1}, signed)
and nib.ITEM_NO <= convert(#{item_no2}, signed)
and nib.ITEM_DETAIL_NO >= convert(#{item_detail_no1}, signed)
and nib.ITEM_DETAIL_NO <= convert(#{item_detail_no2}, signed)
and noc.ORG_CODE_IDX = nib.ORG_CODE_IDX and npc.POSSESSION_CODE_IDX = nib.POSSESSION_CODE_IDX and nib.ITEM_IDX = ni.ITEM_IDX order by rownum desc LIMIT #{perPageNum} OFFSET #{pageStart};

mapper.update:
update nam_image set
	rep_image = 'N'
	where item_idx = #{item_idx}

mapper.delete:
delete from nam_image
where image_idx in (

		#{item}

	)

mapper.update:
