mapper.select:
select row_number() over(order by ns.reg_date) as rownum, ns.item_idx as item_idx, npc.POSSESSION_NM, noc.ORG_NM, ns.ITEM_NO as item_no, ns.ITEM_DETAIL_NO as ITEM_DETAIL_NO, ni.IMAGE_NM, ni.IMAGE_PATH, ni.PUBLIC_SERVICE , ni.image_idx from nam_item_base ns, nam_involvement nv, nam_possession_code npc, nam_org_code noc, nam_image ni where ns.item_no between convert(#{item_no1}, signed) and convert(#{item_no2}, signed) and ns.reg_state = #{reg_state}
and item_detail_no between convert(#{item_detail_no1}) and convert(#{item_detail_no2})
and ns.ORG_CODE_IDX = #{org_code_idx} and ns.POSSESSION_CODE_IDX = #{possession_code_idx} and ns.ITEM_IDX = nv.ITEM_IDX and nv.ORG_CODE_IDX = noc.ORG_CODE_IDX and nv.POSSESSION_CODE_IDX = npc.POSSESSION_CODE_IDX and ni.ITEM_IDX = nv.ITEM_IDX LIMIT #{perPageNum} OFFSET #{pageStart};

select count(*) from nam_item_base ns, nam_involvement nv, nam_possession_code npc, nam_org_code noc, nam_image ni where ns.item_no between convert(#{item_no1}, signed) and convert(#{item_no2}, signed) and ns.reg_state = #{reg_state}
and item_detail_no between convert(#{item_detail_no1}) and convert(#{item_detail_no2})
and ns.ORG_CODE_IDX = #{org_code_idx} and ns.POSSESSION_CODE_IDX = #{possession_code_idx} and ns.ITEM_IDX = nv.ITEM_IDX and nv.ORG_CODE_IDX = noc.ORG_CODE_IDX and nv.POSSESSION_CODE_IDX = npc.POSSESSION_CODE_IDX and ni.ITEM_IDX = nv.ITEM_IDX

mapper.delete:
delete from nam_image
where image_idx in (

		#{item}

	)

mapper.update:
update nam_image set
	image_desc = #{image_desc}
where image_idx in (

		#{item}

	)
