mapper.select:
select row_number() over(order by ib.reg_date) as rownum, ib.item_idx, ib.item_no, ib.item_detail_no, ib.item_nm, npc.POSSESSION_NM, noc.ORG_NM from nam_item_base ib, nam_involvement ni, nam_possession_code npc, nam_org_code noc where ib.item_no between ${item_no1} and ${item_no2}
and ib.item_detail_no between ${item_detail_no1} and ${item_detail_no2}
and ib.item_idx = ni.item_idx and ni.POSSESSION_CODE_IDX = npc.POSSESSION_CODE_IDX and ni.ORG_CODE_IDX = noc.ORG_CODE_IDX LIMIT #{perPageNum} OFFSET #{pageStart};

select count(ib.item_idx)
from nam_item_base ib,
	nam_involvement ni,
	nam_possession_code npc, 
	nam_org_code noc
where ib.item_no between ${item_no1} and ${item_no2}

and ib.item_detail_no between ${item_detail_no1} and ${item_detail_no2}

and ib.item_idx = ni.item_idx
and ni.POSSESSION_CODE_IDX = npc.POSSESSION_CODE_IDX 
and ni.ORG_CODE_IDX = noc.ORG_CODE_IDX

