mapper.select:
select row_number() over(order by ib.item_no) as rownum, ib.item_idx, ib.item_no, ib.item_detail_no, ib.item_nm, npc.POSSESSION_NM, noc.ORG_NM from nam_item_base ib, nam_involvement ni, nam_possession_code npc, nam_org_code noc where ib.item_no between convert(#{item_no1}, signed) and convert(#{item_no2}, signed)
and ib.item_detail_no between convert(#{item_detail_no1}, signed) and convert(#{item_detail_no2}, signed)
and ib.item_idx = ni.item_idx and ni.POSSESSION_CODE_IDX = npc.POSSESSION_CODE_IDX and ni.ORG_CODE_IDX = noc.ORG_CODE_IDX group by ib.item_idx order by rownum desc LIMIT #{perPageNum} OFFSET #{pageStart};

mapper.insert:
insert into nam_erasure_request ( REG_DATE, ERASURE_ITEM, ORG_NM, POSSESSION_NM, ITEM_NO, ITEM_DETAIL_NO, ITEM_NM, ERASURE_REASON, APPROVAL_STATE, REG_USER ) values
( now(),
#{item}
)

mapper.select:
select * from nam_item_base where item_no = #{item_no} 

	and reg_state = #{reg_state}


	and item_detail_no = #{item_detail_no}


	and possession_code_idx = #{possession_code_idx}


	and org_code_idx = #{org_code_idx}

mapper.update:
update nam_item_base set item_no = item_no+1 
where item_no in (
	select * from (
	select item_no from nam_item_base 
	where item_no >= #{item_no}
	) as A	
);

mapper.insert:
