mapper.select:
SELECT R1.* FROM( SELECT row_number() over(order by notice_idx desc) as rnum ,nn.notice_idx ,nn.notice_title ,nn.notice_content ,nn.count ,nn.reg_user ,DATE_FORMAT(nn.reg_date, '%Y-%m-%d %H:%i') AS reg_date ,nn.mod_user ,DATE_FORMAT(nn.mod_date, '%Y-%m-%d %H:%i') AS mod_date FROM nam_notice nn WHERE 1=1
AND (nn.notice_title LIKE CONCAT('%', #{search_word}, '%'))
AND (nn.reg_user LIKE CONCAT('%', #{search_word}, '%'))
AND (nn.notice_title LIKE CONCAT('%', #{search_word}, '%') OR nn.reg_user LIKE CONCAT('%', #{search_word}, '%'))
) R1 LIMIT #{perPageNum} OFFSET #{pageStart}
