mapper.select:
select row_number() over(order by ns.reg_date) as rownum, ns.speciality_idx, ns.item_idx, ns.speciality_code_idx, ns.title, ns.file_nm, ns.file_path, ns.copyright, ns.content, ns.reg_user, DATE_FORMAT(ns.reg_date, '%Y-%m-%d') AS reg_date, ns.mod_user, DATE_FORMAT(ns.mod_date, '%Y-%m-%d') AS mod_date, nsc.speciality_nm from nam_speciality ns, nam_speciality_code nsc where ns.item_idx = #{item_idx} and ns.speciality_code_idx = nsc.speciality_code_idx
and ns.title like CONCAT('%',#{speciality_title_search},'%')
and ns.speciality_code_idx = #{speciality_code_idx_search}
order by rownum desc LIMIT #{perPageNum} OFFSET #{pageStart};

mapper.insert:
insert into nam_speciality (
	item_idx, speciality_code_idx, title, file_nm, file_path, copyright, content, reg_user, reg_date, mod_user
)
values (
	#{item_idx}, #{speciality_code_idx},
	#{spc_title}, #{spc_file_nm}, #{spc_file_path}, #{spc_copyright}, #{spc_content}, #{reg_user}, now(), #{mod_user}
)
