mapper.select:
select ib.rownum, ib.item_idx, ib.rownum, ib.item_no, ib.item_detail_no, ib.item_nm, ib.item_se_nm, ib.item_eng_nm, ib.author, ib.reg_state, ib.possession_nm, ib.org_nm from ( select row_number() over(order by ib.item_no*1, ib.item_detail_no*1) rownum, ib.item_idx, ib.item_no, ib.item_detail_no, ib.item_nm, ib.item_se_nm, ib.item_eng_nm, ib.author, ib.reg_state, npc.possession_nm, noc.org_nm from nam_item_base ib, nam_involvement ni, nam_possession_code npc, nam_org_code noc where ib.org_code_idx = #{org_code_idx}
and ib.possession_code_idx = #{possession_code_idx}
and ib.ITEM_NO >= convert(#{item_no1}, signed)
and ib.ITEM_NO <= convert(#{item_no2}, signed)
and ib.ITEM_DETAIL_NO >= convert(#{item_detail_no1}, signed)
and ib.ITEM_DETAIL_NO <= convert(#{item_detail_no2}, signed)
and ib.reg_state = #{reg_state}
and ib.reg_state = #{reg_state}
and ib.item_idx = ni.item_idx and ni.POSSESSION_CODE_IDX = npc.POSSESSION_CODE_IDX and ni.ORG_CODE_IDX = noc.ORG_CODE_IDX ) ib order by rownum desc LIMIT #{perPageNum} OFFSET #{pageStart}

select ib.rownum, ib.item_idx, ib.rownum, ib.item_no, ib.item_detail_no, ib.item_nm, ib.item_se_nm, ib.item_eng_nm, ib.author, ib.reg_state, ib.possession_nm, ib.org_nm from ( select row_number() over(order by ib.item_no*1, ib.item_detail_no*1) rownum, ib.item_idx, ib.item_no, ib.item_detail_no, ib.item_nm, ib.item_se_nm, ib.item_eng_nm, ib.author, ib.reg_state, npc.possession_nm, noc.org_nm from nam_item_base ib, nam_involvement ni, nam_possession_code npc, nam_org_code noc where ib.org_code_idx = #{org_code_idx}
and ib.possession_code_idx = #{possession_code_idx}
and ib.ITEM_NO >= convert(#{item_no1}, signed)
and ib.ITEM_NO <= convert(#{item_no2}, signed)
and ib.ITEM_DETAIL_NO >= convert(#{item_detail_no1}, signed)
and ib.ITEM_DETAIL_NO <= convert(#{item_detail_no2}, signed)
and ib.reg_state = #{reg_state}
and ib.reg_state = #{reg_state}
and ib.item_idx = ni.item_idx and ni.POSSESSION_CODE_IDX = npc.POSSESSION_CODE_IDX and ni.ORG_CODE_IDX = noc.ORG_CODE_IDX ) ib order by rownum desc LIMIT #{perPageNum} OFFSET #{pageStart}

select count(*) from ( select ib.item_idx, ib.item_no, ib.item_detail_no, ib.item_nm, ib.item_se_nm, ib.item_eng_nm, ib.author, ib.reg_state, npc.possession_nm, noc.org_nm from nam_item_base ib, nam_involvement ni, nam_possession_code npc, nam_org_code noc where ib.org_code_idx = #{org_code_idx}
and ib.possession_code_idx = #{possession_code_idx}
and ib.ITEM_NO >= convert(#{item_no1}, signed)
and ib.ITEM_NO <= convert(#{item_no2}, signed)
and ib.ITEM_DETAIL_NO >= convert(#{item_detail_no1}, signed)
and ib.ITEM_DETAIL_NO <= convert(#{item_detail_no2}, signed)
and ib.reg_state = #{reg_state}
and ib.reg_state = #{reg_state}
and ib.item_idx = ni.item_idx and ni.POSSESSION_CODE_IDX = npc.POSSESSION_CODE_IDX and ni.ORG_CODE_IDX = noc.ORG_CODE_IDX ) ib

mapper.update:
update nam_item_base
	set reg_state = #{reg_state}
where item_idx  in 

	#{item}
