mapper.select:
SELECT R1.* FROM( SELECT row_number() over(order by member_idx desc) as rnum ,nm.member_idx ,nm.member_id ,nm.org_code_idx ,nm.group_idx ,nm.member_pw ,nm.member_nm ,nm.remark ,nm.reg_user ,nm.reg_date ,nm.mod_user ,nm.mod_date ,nm.enabled ,ng.group_nm ,ng.admin FROM nam_member nm left join nam_group ng on nm.GROUP_IDX =ng.GROUP_IDX WHERE 1=1 AND MEMBER_IDX = #{member_idx}
AND (nm.member_id LIKE CONCAT('%', #{search_word}, '%'))
AND (nm.member_nm LIKE CONCAT('%', #{search_word}, '%'))
AND (ng.group_nm LIKE CONCAT('%', #{search_word}, '%'))
AND (nm.remark LIKE CONCAT('%', #{search_word}, '%'))
AND (nm.enabled LIKE CONCAT('%', #{search_word}, '%'))
AND (nm.member_id LIKE CONCAT('%', #{search_word}, '%') OR nm.member_nm LIKE CONCAT('%', #{search_word}, '%') OR ng.group_nm LIKE CONCAT('%', #{search_word}, '%') OR nm.remark LIKE CONCAT('%', #{search_word}, '%') OR nm.enabled LIKE CONCAT('%', #{search_word}, '%'))
) R1 LIMIT #{perPageNum} OFFSET #{pageStart}

mapper.insert:

 INSERT INTO nam_member 
       ( MEMBER_ID
       , ORG_CODE_IDX
       , GROUP_IDX
       , MEMBER_PW
       , MEMBER_NM
       , REMARK
       , REG_USER
       , REG_DATE
       , ENABLED)
VALUES ( #{member_id}
       , '1'
       , #{group_idx}
       , #{member_pw}
       , #{member_nm}
       , #{remark}
       , #{reg_user}
       , now()
       , 'Y')

mapper.update:

UPDATE nam_member 
   SET MEMBER_ID=#{member_id}
     , ORG_CODE_IDX='1'
     , GROUP_IDX=#{group_idx}
     , MEMBER_NM=#{member_nm}
     , REMARK=#{remark}
     , MOD_USER=#{mod_user}
     , MOD_DATE=#{mod_date}
     , ENABLED=#{enabled}
 WHERE MEMBER_IDX=#{member_idx}
